import { TextField } from "@material-ui/core"

const DateInput = ({type,title,day,setDay}) => {

    const date =(new Date(new Date().toLocaleString('en-US', { timeZone: 'Etc/GMT-6' })).toISOString()).split(":").slice(0,2).join(":");

    return (
    <>
    <br/>
    <TextField
        className = "input"
        id={type}
        label={title}
        type={type}
        defaultValue={date}
        InputLabelProps={{
        shrink: true,
        }}
        value={day}
        onChange={(e) => setDay(e.target.value)}
    />
    </>
    )
}

export default DateInput
