import { TextField } from "@material-ui/core"

const MainInput = ({type,title}) => {

    const date =(new Date(new Date().toLocaleString('en-US', { timeZone: 'Etc/GMT-6' })).toISOString()).split(":").slice(0,2).join(":");

    return (
    <>
    <TextField
        id={type}
        label={title}
        type={type}
        defaultValue={date}
        InputLabelProps={{
        shrink: true,
        }}
    />
    <br/>
    </>
    )
}

export default MainInput
